import React, { useEffect } from 'react';
import RelatedVideo from './RelatedVideoListItem';
import RelatedVideoListItem from './RelatedVideoListItem';
import { useDispatch, useSelector } from 'react-redux';
import { fetchRelatedVideos } from '../../features/relatedVideos/relatedVideosSlice';
import Loading from '../ui/Loading';

const RelatedVideoList = ({ currentVideoId, tags }) => {
    const dispatch = useDispatch();
    const { relatedVideos, isLoading, isError, error } = useSelector(((state) => state.relatedVideos))
    useEffect(() => {
        dispatch(fetchRelatedVideos({ tags, id: currentVideoId }))
    }, [dispatch, tags, currentVideoId]);

    //decide what to render
    let content = null;
    if (isLoading) {
        content = <Loading />
    }
    if (!isLoading && isError) {
        content = <div className="col-span-12">{error}</div>
    }
    if (!isLoading && !isError && videos ?.length === 0) {
        content = <div className="col-span-12">No  videos found!</div>
    }
    if (!isLoading && !isError && videos ?.length > 0) {
        content = relatedVideos.map((video) => <RelatedVideoListItem
            key={video.id} video={video}
        />)
    }

    return (
        <div>
            <div
                class="col-span-full lg:col-auto max-h-[570px] overflow-y-auto"
            >
                {/* <!-- single related video --> */}
                {/* <div class="w-full flex flex-row gap-2 mb-4">
                    <RelatedVideoListItem></RelatedVideoListItem>
                    
                </div> */}
                {content}
            </div>

        </div>
    );
};

export default RelatedVideoList;
